// Get the URL string from the aggregate node
const urlString = $json.url;

// Split the comma-separated URLs and format each with quotes
const formattedUrls = urlString
  .flatMap(urlString => {
    if (typeof urlString === "string") {
      return urlString.split(',');
    }
    return [];
  })
  .map(url => `"${url.trim()}"`)
  .join(',\n');

// Return the formatted result
return {
  formattedUrls: formattedUrls
};

