// Get upload_assets (can be string or array)
const upload = $input.first().json.upload_assets;

// Normalize to an array
const parts = Array.isArray(upload) ? upload : [upload];

// Build a flat list of URLs
const urls = parts
  .filter(v => typeof v === 'string')
  .flatMap(s => s.split(','))        // if your env complains about flatMap, use the reduce version below
  .map(s => s.trim())
  .filter(Boolean);

// Format with quotes + new lines
const formattedUrls = urls.map(u => `"${u}"`).join(',\n');

return { formattedUrls, urls };

